    const textSplit = (text) => {
        return text.split('\n')
    }

    const photoNext = () => {
        if (photoNum < gallery.length - 1) {
            setPhotoNum(photoNum + 1)
        } else {
            setPhotoNum(0)
        }
    }

    const photoPrev = () => {
        if (photoNum > 0) {
            setPhotoNum(photoNum - 1)
        } else {
            setPhotoNum(gallery.length - 1)
        }
    }

    const showModal = () => {
        document.querySelector('.FeedbackModal').classList.remove('None')
    }

