import React, {useEffect, useState} from 'react';
import {useNavigate} from "react-router-dom";
import { LuPlus } from "react-icons/lu";

import '../styles/admin.scss'
import {addService, fetchImages, fetchIntensives, fetchPrices, fetchRates, fetchServices} from "../http/innoAPI";

const Admin = () => {
    const navigate = useNavigate()
    const [password, setPassword] = useState('')
    const [isAdmin, setIsAdmin] = useState(false)
    const [category, setCategory] = useState('')
    const [services, setServices] = useState()
    const [prices, setPrices] = useState()
    const [intensives, setIntensives] = useState()
    const [rates, setRates] = useState()
    const [images, setImages] = useState()
    const [name, setName] = useState('')
    const [text, setText] = useState('')
    const [price, setPrice] = useState('')
    const [categoryId, setCategoryId] = useState('')
    const [date, setDate] = useState('')

    const handleNavigate = (e) => {
        window.scrollTo({
            top: 0,
            left: 0
        })
        navigate(`${e.target.id}`)
    }

    const submitPassword = () => {
        if (password === process.env.REACT_APP_ADMIN) {
            setIsAdmin(true)
        }
    }

    const handlePassword = (e) => {
        setPassword(e.target.value)
    }

    const chooseCategory = (e) => {
        setCategory(e.target.id)
    }

    const handleName = (e) => {
        setName(e.target.value)
    }

    const handleText = (e) => {
        setText(e.target.value)
    }

    const handlePrice = (e) => {
        setPrice(e.target.value)
    }

    const createService = () => {
        if (name.length > 0 && text.length > 0) {
            addService(name, text).then(() => {
                setServices(null)
                setName('')
                setText('')
                fetchServices().then(data => {
                    setServices(data)
                })
            })
        }
    }

    useEffect(() => {
        fetchServices().then(data => {
            setServices(data)
        })
        fetchRates().then(data => {
            setRates(data)
        })
        fetchPrices().then(data => {
            setPrices(data)
        })
        fetchIntensives().then(data => {
            setIntensives(data)
        })
        fetchImages().then(data => {
            setImages(data)
        })
    }, [])

    return (
        <div className="ContentContainer">
            <div className="TopLink">
                <div className="Link" id="/" onClick={handleNavigate}>Главная</div>
                <div>&nbsp;/ Панель администратора</div>
            </div>
            <h1 className="AdminSub">Панель администратора</h1>
            {!isAdmin ?
                <>
                    <input className="AdminPassword" type="password" value={password} onChange={handlePassword} onKeyPress={(e) => {
                                    if (e.key === "Enter") {
                                        submitPassword();
                                    }
                                }} />
                    <button className="AdminSubmit" onClick={submitPassword}>ВОЙТИ</button>
                </>
                :
                <>
                    {category === '' ?
                        <>
                            <button className="AdminCategory" id="service" onClick={chooseCategory}>Услуги</button>
                            <button className="AdminCategory" id="price" onClick={chooseCategory}>Прайс</button>
                            <button className="AdminCategory" id="intensive" onClick={chooseCategory}>Интенсивы</button>
                            <button className="AdminCategory" id="rate" onClick={chooseCategory}>Отзывы</button>
                            <button className="AdminCategory" id="image" onClick={chooseCategory}>Фотогалерея</button>
                        </>
                        :
                        (category === 'service') ?
                            <>
                                <h3 className="AdminPanelSub">Услуги</h3>
                                {services ?
                                    <>
                                        <div className="ItemTip">Создание новой услуги</div>
                                        <input className="AdminInput" type="text" placeholder="Название услуги" value={name} onChange={handleName} />
                                        <textarea className="AdminInput" placeholder="Описание" value={text} onChange={handleText}></textarea>
                                        <button className="AdminCategory" id="image" onClick={createService}><LuPlus size={20} /></button>
                                        {services.map((item, i) => {
                                            return (
                                                <div className="ItemBox" key={item.id}>
                                                    <div className="ItemBoxSub">Услуга {i + 1}</div>
                                                    <div className="ItemTip">Название</div>
                                                    <div className="ItemText">{item.name}</div>
                                                    <div className="ItemTip">Описание</div>
                                                    <div className="ItemText">{item.text}</div>
                                                    <button className="ItemChange">Редактировать</button>
                                                </div>
                                            )
                                        })}
                                    </>
                                    :
                                    <div className='LoaderBox'>
                                        <span className="Loader"></span>
                                    </div>
                                }
                            </>
                            :
                            (category === 'price') ?
                                <>
                                    <h3 className="AdminPanelSub">Прайс</h3>
                                </>
                                :
                                (category === 'intensive') ?
                                    <>
                                        <h3 className="AdminPanelSub">Интенсивы</h3>
                                    </>
                                    :
                                    (category === 'rate') ?
                                        <>
                                            <h3 className="AdminPanelSub">Отзывы</h3>
                                        </>
                                        :
                                        (category === 'image') &&
                                            <>
                                                <h3 className="AdminPanelSub">Фотогалерея</h3>
                                            </>
                    }
                </>
            }
        </div>
    )
}

export default Admin
