import { $host } from '.'

// fetch

export const fetchServices = async () => {
    const { data } = await $host.get('api/service')
    return data
}

export const fetchPrices = async () => {
    const { data } = await $host.get('api/price')
    return data
}

export const fetchIntensives = async () => {
    const { data } = await $host.get('api/intensive')
    return data
}

export const fetchImages = async () => {
    const { data } = await $host.get('api/image')
    return data
}

export const fetchRates = async () => {
    const { data } = await $host.get('api/rate')
    return data
}

// create

export const addService = async (name, text) => {
    const { data } = await $host.post('api/service', {name, text})
    return data
}

export const addPrice = async (category_id, name, text, price) => {
    const { data } = await $host.post('api/price', {category_id, name, text, price})
    return data
}

export const addIntensive = async (name, text, price) => {
    const { data } = await $host.post('api/intensive', {name, text, price})
    return data
}

export const addImage = async (text) => {
    const { data } = await $host.post('api/image', {text})
    return data
}

export const addRate = async (date, name, text) => {
    const { data } = await $host.post('api/rate', {date, name, text})
    return data
}

// update

export const updateService = async (name, text) => {
    const { data } = await $host.post('api/service/update', {name, text})
    return data
}

export const updatePrice = async (category_id, name, text, price) => {
    const { data } = await $host.post('api/price/update', {category_id, name, text, price})
    return data
}

export const updateIntensive = async (name, text, price) => {
    const { data } = await $host.post('api/intensive/update', {name, text, price})
    return data
}

export const updateRate = async (date, name, text) => {
    const { data } = await $host.post('api/rate/update', {date, name, text})
    return data
}
